sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   var requestTypeField = g_form.getControl('u_request_type');
	var whatNeededField = g_form.getControl('u_what_needed');

	if (whatNeededField){
		clearChoices('u_what_needed');

		var requestType = g_form.getValue('u_request_type');
		console.log('requestType: ', requestType);

		populateWhatNeededChoices(requestType);
	}

}

function clearChoices(fieldName){
	var selectElement = g_form.getControl(fieldName);
	if (selectElement){
		while (selectElement.options.length > 0){
			selectElement.remove(0);
		}
	}
}

function populateWhatNeededChoices(requestType){
	var whatNeededField = g_form.getControl('u_what_needed');

	if(whatNeededField){
		if(requestType === 'hr'){
			g_form.addOption('u_what_needed', 'human_resources', 'Human Resources 1');
			g_form.addOption('u_what_needed', 'facilities_2', 'Facilities 2');
			g_form.addOption('u_what_needed', 'other', 'Other');
		}
		else if(requestType === 'facilities'){
			g_form.addOption('u_what_needed', 'facilities_1', 'Facilities 1');
			g_form.addOption('u_what_needed', 'facilities_2', 'Facilities 2');
			g_form.addOption('u_what_needed', 'other', 'Other');
		}
		else{
			g_form.addOption('u_what_needed', 'legal_1', 'Legal 1');
			g_form.addOption('u_what_needed', 'legal_2', 'Legal 2');
			g_form.addOption('u_what_needed', 'other', "Other");
		}
	}
}
